'create or replace PACKAGE BODY BANK_CUSTOMER_PKG AS',
'',
'  PROCEDURE add_customer(',
'    p_name        IN VARCHAR2,',
'    p_email       IN VARCHAR2,',
'    p_phone       IN VARCHAR2,',
'    p_address     IN VARCHAR2,',
'    p_gender      IN VARCHAR2,',
'    p_created_by  IN NUMBER,',
'    p_created_on  IN DATE',
'  ) IS',
'    v_exists NUMBER;',
'  BEGIN',
'',
'    SELECT COUNT(*) INTO v_exists',
'    FROM bank_customers',
'    WHERE email = p_email;',
'',
'    IF v_exists > 0 THEN',
'      RAISE_APPLICATION_ERROR(-20001, ''Customer already exists.'');',
'    END IF;',
'',
'    INSERT INTO bank_customers (',
'      full_name, email, phone, address, gender, created_by, created_on',
'    ) VALUES (',
'      p_name, p_email, p_phone, p_address, p_gender, p_created_by, SYSDATE',
'    );',
'  END;',
'',
'  PROCEDURE update_customer(',
'    p_customer_id IN NUMBER,',
'    p_name        IN VARCHAR2,',
'    p_email       IN VARCHAR2,',
'    p_phone       IN VARCHAR2,',
'    p_address     IN VARCHAR2,',
'    p_gender      IN VARCHAR2',
'  ) IS',
'  BEGIN',
'    UPDATE bank_customers',
'    SET full_name   = UPPER(p_name),',
'        email   = p_email,',
'        phone   = p_phone,',
'        address = p_address,',
'        gender  = p_gender',
'    WHERE customer_id = p_customer_id;',
'  END;',
'',
'  FUNCTION get_customer(p_customer_id IN NUMBER) RETURN SYS_REFCURSOR IS',
'    v_cursor SYS_REFCURSOR;',
'  BEGIN',
'    OPEN v_cursor FOR',
'      SELECT customer_id, full_name, email, phone, address, gender',
'      FROM bank_customers',
'      WHERE customer_id = p_customer_id;',
'    RETURN v_cursor;',
'  END;',
'',
'  FUNCTION delete_customer(p_customer_id IN NUMBER) RETURN VARCHAR2 IS',
'  v_exists NUMBER;',
'BEGIN',
'  SELECT COUNT(*) INTO v_exists',
'  FROM bank_customers',
'  WHERE customer_id = p_customer_id;',
'',
'  IF v_exists = 0 THEN',
'    RETURN ''Customer not found.'';',
'  END IF;',
'',
'  DELETE FROM bank_customers',
'  WHERE customer_id = p_customer_id;',
'',
'  RETURN ''Customer deleted successfully.'';',
'END;',
'',
'',
'END BANK_CUSTOMER_PKG;',
