'            INSERT INTO bank_transactions (',
'            account_number,  transaction_type,amount, transaction_date,  remarks, performed_by',
'            ) VALUES (',
'              p_account_number,  ''Deposit'',p_amount, SYSDATE, p_remark,p_teller_id',
'            );',
'           ',
'        ELSE',
'            DBMS_OUTPUT.PUT_LINE(''Amount must be positive'');',
'        END IF;',
'    ELSE',
'        DBMS_OUTPUT.PUT_LINE(''Account does not exist'');',
'    END IF;',
'    ',
'  END;',
'',
'  PROCEDURE withdraw(p_account_number IN VARCHAR2, p_amount IN NUMBER, p_teller_id IN NUMBER,  p_remark IN VARCHAR2) IS',
'    v_count NUMBER;',
'    v_balance NUMBER;',
'  BEGIN',
'     SELECT COUNT(*) INTO v_count',
'    FROM bank_accounts',
'    WHERE account_number = p_account_number;',
'   ',
'    ',
'    IF v_count > 0 THEN',
'         SELECT balance INTO v_balance FROM bank_accounts',
'         WHERE account_number = p_account_number;',
'        IF p_amount <= v_balance THEN',
'           UPDATE bank_accounts',
'            SET balance = balance - p_amount',
'            WHERE account_number = p_account_number;',
'',
'            INSERT INTO bank_transactions (',
'            account_number,  transaction_type,amount, transaction_date, performed_by, remarks',
'            ) VALUES (',
'              p_account_number,  ''Withdrawal'',p_amount,SYSDATE, p_teller_id,p_remark',
'            );',
'            APEX_APPLICATION.G_PRINT_SUCCESS_MESSAGE := ''Transfer completed successfully'';',
'        ELSE',
'            apex_error.add_error(',
'            p_message => ''Insufficient Funds'',',
'            p_display_location => apex_error.c_inline_in_notification',
');',
'        END IF;',
'    ELSE',
'        DBMS_OUTPUT.PUT_LINE(''Invalid Account'');',
'    END IF;',
'    ',
'  END;',
'',
' PROCEDURE transfer(',
'    p_from_account IN VARCHAR2,',
'    p_to_account   IN VARCHAR2,',
'    p_amount       IN NUMBER,',
'    p_teller_id    IN NUMBER,',
'    p_remark       IN VARCHAR2',
'  ) IS',
'    v_from_count   NUMBER;',
'    v_to_count     NUMBER;',
'    v_balance      NUMBER;',
' BEGIN',
'    -- Check if FROM account exists',
'    SELECT COUNT(*) INTO v_from_count',
'    FROM bank_accounts  -- Fixed table name consistency',
'    WHERE account_number = p_from_account;  -- Fixed parameter name',
'    ',
'    IF v_from_count > 0 THEN',
'        -- Check if TO account exists',
'        SELECT COUNT(*) INTO v_to_count',
'        FROM bank_accounts',
'        WHERE account_number = p_to_account;',
'        IF p_from_account != p_to_account THEN',
'            IF v_to_count > 0 THEN',
'            -- Get balance from sender account',
'             SELECT balance INTO v_balance ',
'             FROM bank_accounts',
'             WHERE account_number = p_from_account;',
'            ',
'                IF p_amount <= v_balance THEN',
'                 -- Deduct from sender',
'                 UPDATE bank_accounts',
'                 SET balance = balance - p_amount',
'                 WHERE account_number = p_from_account;',
'',
'                 -- Credit receiver',
'                 UPDATE bank_accounts',
'                 SET balance = balance + p_amount',
'                 WHERE account_number = p_to_account;',
'',
'                 -- Insert transaction for sender',
'                 INSERT INTO bank_transactions (',
'                    account_number, transaction_type, amount, ',
'                    transaction_date, performed_by, remarks',
'                 ) VALUES (',
'                    p_from_account, ''Transfer'', p_amount, ',
'                    SYSDATE, p_teller_id, ''Transfer Sent''',
'                 );',
'                ',
'                 -- Insert transaction for receiver',
'                 INSERT INTO bank_transactions (',
'                    account_number, transaction_type, amount, ',
'                    transaction_date, performed_by, remarks',
'                 ) VALUES (',
'                    p_to_account, ''Transfer'', p_amount,',
'                    SYSDATE, p_teller_id, ''Transfer Received''',
'                 );',
'                ',
'                 DBMS_OUTPUT.PUT_LINE(''Transfer completed successfully'');',
'                ',
'                 ELSE',
'                 DBMS_OUTPUT.PUT_LINE(''Insufficient Funds to Make Transfer'');',
'                END IF;',
'             ELSE',
'             DBMS_OUTPUT.PUT_LINE(''Invalid Destination Account'');',
'            END IF;',
'         ELSE',
'         DBMS_OUTPUT.PUT_LINE (''Cannot Transer to the Same account!'');',
'        END IF;',
'     ELSE',
'        DBMS_OUTPUT.PUT_LINE(''Invalid Source Account'');',
'    END IF;',
'  END;',
'    ',
'   ',
'END BANK_TRANSACTION_PKG;',
'/',
'create or replace PACKAGE BODY BANK_USER_PKG AS',
'',
'  PROCEDURE create_user(',
'    p_username IN VARCHAR2,',
'    p_password IN VARCHAR2,',
'    p_role     IN VARCHAR2,',
'    p_status   IN VARCHAR2',
'  ) IS',
'    v_exists NUMBER;',
'    v_validation_msg VARCHAR2(200);',
'    v_hashed_password VARCHAR2(64);',
'  BEGIN',
'    -- Check if username already exists',
'    SELECT COUNT(*) INTO v_exists',
'    FROM bank_users',
'    WHERE username = p_username;',
'',
'    IF v_exists > 0 THEN',
'      RAISE_APPLICATION_ERROR(-20001, ''Username already exists.'');',
'    END IF;',
'',
'    -- Validate password',
'    v_validation_msg := validate_password(p_password);',
'    IF v_validation_msg IS NOT NULL THEN',
'      RAISE_APPLICATION_ERROR(-20004, v_validation_msg);',
'    END IF;',
'',
'    -- Hash password',
'    v_hashed_password := hash_password(p_password);',
'',
'    -- Insert new user',
'    INSERT INTO bank_users (username, password, role, status)',
'    VALUES (p_username, v_hashed_password, p_role, p_status);',
'',
'    DBMS_OUTPUT.PUT_LINE(''User Successfully Created'');',
'  END create_user;',
'',
'  FUNCTION login(',
'    p_username IN VARCHAR2,',
'    p_password IN VARCHAR2',
'  ) RETURN BOOLEAN IS',
'    v_stored_hash VARCHAR2(64);',
'  BEGIN',
'    SELECT password INTO v_stored_hash',
'    FROM bank_users',
'    WHERE UPPER(username) = UPPER(p_username)',
'      AND status = ''Active'';',
'',
'    RETURN verify_password(p_password, v_stored_hash);',
'',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      RETURN FALSE;',
'    WHEN OTHERS THEN',
'      RETURN FALSE;',
'  END login;',
'',
'  FUNCTION get_user_id(p_username IN VARCHAR2) RETURN NUMBER IS',
'    v_id NUMBER;',
'  BEGIN',
'    SELECT user_id INTO v_id ',
'    FROM bank_users ',
'    WHERE username = p_username;',
'',
'    RETURN v_id;',
'',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      RETURN NULL;',
'    WHEN OTHERS THEN',
'      RAISE;',
'  END get_user_id;',
'',
'  FUNCTION get_user_role(p_username IN VARCHAR2) RETURN VARCHAR2 IS',
'    v_role VARCHAR2(20);',
'  BEGIN',
'    SELECT role INTO v_role ',
'    FROM bank_users ',
'    WHERE username = p_username;',
'',
'    RETURN v_role;',
'',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      RETURN NULL;',
'    WHEN OTHERS THEN',
'      RAISE;',
'  END get_user_role;',
'',
'  PROCEDURE update_status(',
'    p_user_id IN NUMBER,',
'    p_status  IN VARCHAR2',
'  ) IS',
'    v_count NUMBER;',
'  BEGIN',
'    -- Check if user exists before updating',
'    SELECT COUNT(*) INTO v_count',
'    FROM bank_users',
'    WHERE user_id = p_user_id;',
'',
'    IF v_count = 0 THEN',
'      RAISE_APPLICATION_ERROR(-20002, ''User ID not found.'');',
'    END IF;',
'',
'    UPDATE bank_users',
'    SET status = p_status',
'    WHERE user_id = p_user_id;',
'  END update_status;',
'',
'  FUNCTION validate_password(p_password IN VARCHAR2) RETURN VARCHAR2 IS',
'  BEGIN',
'    IF LENGTH(p_password) < 8 OR LENGTH(p_password) > 15 THEN',
'      RETURN ''Password length must be between 8 and 15 characters.'';',
'    ELSIF NOT REGEXP_LIKE(p_password, ''.*[0-9]'') THEN',
'      RETURN ''Password must contain at least one number.'';',
'    ELSIF NOT REGEXP_LIKE(p_password, ''.*[a-z]'') THEN',
'      RETURN ''Password must contain at least one lowercase letter.'';',
'    ELSIF NOT REGEXP_LIKE(p_password, ''.*[A-Z]'') THEN',
'      RETURN ''Password must contain at least one uppercase letter.'';',
'    ELSIF NOT REGEXP_LIKE(p_password, ''.*[!@#$%^&*()_]'', ''c'') THEN',
'      RETURN ''Password must contain at least one special character (!@#$%^&*()_).'';',
'    END IF;',
'    RETURN NULL;',
'  END;',
'',
'  FUNCTION hash_password(p_password IN VARCHAR2) RETURN VARCHAR2 IS',
'  BEGIN',
'    RETURN STANDARD_HASH(p_password, ''SHA256'');',
'  END;',
'',
'  FUNCTION verify_password(p_password IN VARCHAR2, p_hash IN VARCHAR2) RETURN BOOLEAN IS',
'  BEGIN',
'    RETURN hash_password(p_password) = UPPER(p_hash);',
'  END;',
'',
'  PROCEDURE initialize_session(p_username IN VARCHAR2) IS',
'    v_user_id   NUMBER;',
'    v_user_role VARCHAR2(20);',
'  BEGIN',
'    -- Get user details',
'    v_user_id   := get_user_id(p_username);',
'    v_user_role := get_user_role(p_username);',
'',
'    -- Check if user exists',
'    IF v_user_id IS NULL THEN',
'      RAISE_APPLICATION_ERROR(-20003, ''User not found: '' || p_username);',
'    END IF;',
'',
'    -- Set session state',
'    APEX_UTIL.SET_SESSION_STATE(''APP_USER_ID'', v_user_id);',
'    APEX_UTIL.SET_SESSION_STATE(''APP_USER_ROLE'', v_user_role);',
'    APEX_CUSTOM_AUTH.SET_USER(p_username);',
'  END initialize_session;',
'',
'  PROCEDURE logout IS',
'  BEGIN',
'    -- Clear session variables',
'    APEX_UTIL.SET_SESSION_STATE(''APP_USER_ID'', '''');',
'    APEX_UTIL.SET_SESSION_STATE(''APP_USER_ROLE'', '''');',
'',
'    -- Delete the session',
'    APEX_SESSION.DELETE_SESSION;',
'  END logout;',
'',
'END BANK_USER_PKG;',
